import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';

interface MovementType {
  movement: string,
}


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    formControl: {
      margin: theme.spacing(3),
    },
  }),
);

const MovementSelection = (props: MovementType) => {

  const movementType = props.movement;
  const classes = useStyles();

   
  const [state, setState] = React.useState({
    movement1: false,
    movement2: false,
    movement3: false,
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { movement1, movement2, movement3 } = state;
  const error = [movement1, movement2, movement3].filter((v) => v).length !== 2;

  return (
    <div className={classes.root}>
      <FormControl component="fieldset" className={classes.formControl}>
        <FormLabel component="legend">Assign responsibility</FormLabel>
        <FormControl required error={error} component="fieldset" className={classes.formControl}>
         <FormLabel component="legend">Pick Three {movementType} Movements: </FormLabel>
        <FormGroup>
          <FormControlLabel
              control={<Checkbox checked={movement1} onChange={handleChange} name="${movementType} 1" />}
              label={`1 ${movementType}`}
          />
          <FormControlLabel
            control={<Checkbox checked={movement2} onChange={handleChange} name="${movementType} 2" />}
            label="${movementType} 2"
          />
          <FormControlLabel
            control={<Checkbox checked={movement3} onChange={handleChange} name="$movementType} 2" />}
            label="${movementType} 3"
          />
        </FormGroup>
        <FormHelperText>You can display an error</FormHelperText>
        </FormControl>
        </FormControl>
    </div>
  );
}
export default MovementSelection;
